import React from "react";

const App = () => {
  return (
    <div className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-500 to-indigo-600 text-white">
      <div className="text-center p-10 bg-white bg-opacity-10 backdrop-blur-lg rounded-2xl shadow-lg max-w-md">
        <h1 className="text-4xl font-bold mb-4">Welcome to My React App</h1>
        <p className="text-lg">This is a professional and beautifully designed welcome page.</p>
        <button className="mt-6 px-6 py-3 bg-white text-blue-600 font-semibold rounded-lg shadow-md hover:bg-blue-100 transition">
          Get Started
        </button>
      </div>
    </div>
  );
};

export default App;
