import React, { useState } from 'react';
import '../App.css';

function DiscoverPage() {
  const [data, setData] = useState([
    { id: 1, title: 'Jean ', description: 'lsji jsk', status: 'en cours' , duedate: '13/02/2025' },
    { id: 2, title: 'Marie ', description: 'sqkcsm sqlio', status: 'en cours', duedate: '18/02/2025'  },
    { id: 3, title: 'Pierre ', description: 'ksj nkll', status: 'en cours', duedate: '23/02/2025'}
  ]);

  const handleEdit = (id) => {
    alert(`Modifier l'élément avec l'ID : ${id}`);
  };

  const handleDelete = (id) => {
    setData(data.filter(item => item.id !== id));
  };

  return (
    <div className="container">
        <header className="header">
        <h1>DevOps Project</h1>
        <nav>
          <ul className="nav-list">
            <li><a href="#">Accueil</a></li>
            <li><a href="#">À propos</a></li>
            <li><a href="#">Contact</a></li>
          </ul>
        </nav>
      </header>

      <main className="main-content">
        <h2>Tableau des utilisateurs</h2>
        <table className="table">
          <thead>
            <tr>
              <th>ID</th>
              <th>Title</th>
              <th>Description</th>
              <th>Status</th>
              <th>DueDate</th>
              <th>DueDate</th>
            </tr>
          </thead>
          <tbody>
            {data.map(item => (
              <tr key={item.id}>
                  <td>{item.id}</td>
                <td>{item.title}</td>
                <td>{item.description}</td>
                <td>{item.status}</td>
                <td>{item.duedate}</td>
                <td>
                  <button onClick={() => handleEdit(item.id)} className="btn-edit">Modifier</button>
                  <button onClick={() => handleDelete(item.id)} className="btn-delete">Supprimer</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>

      <footer className="footer">
        &copy; 2025 Mon Projet. Tous droits réservés.
      </footer>
    </div>
  );
}

export default DiscoverPage;
