import React, { useState } from 'react';
import '../App.css';
function DiscoverPage() {
    const data = [
      { id: 1, name: 'Jean Dupont', email: 'jean@example.com', phone: '0123456789' },
      { id: 2, name: 'Marie Curie', email: 'marie@example.com', phone: '9876543210' },
      { id: 3, name: 'Albert Einstein', email: 'albert@example.com', phone: '1231231234' },
    ];
  
    const handleEdit = (id) => {
      alert(`Modification de l'élément avec l'ID : ${id}`);
    };
  
    const handleDelete = (id) => {
      alert(`Suppression de l'élément avec l'ID : ${id}`);
    };
  
    return (
      <div className="discover-page">
        <h1>Page de Découverte</h1>
        <table className="table-auto border-collapse border border-gray-200 w-full">
          <thead>
            <tr>
              <th className="border border-gray-300 p-2">Nom</th>
              <th className="border border-gray-300 p-2">Email</th>
              <th className="border border-gray-300 p-2">Téléphone</th>
              <th className="border border-gray-300 p-2">Actions</th> {/* Colonne Actions */}
            </tr>
          </thead>
          <tbody>
            {data.map((item) => (
              <tr key={item.id}>
                <td className="border border-gray-300 p-2">{item.name}</td>
                <td className="border border-gray-300 p-2">{item.email}</td>
                <td className="border border-gray-300 p-2">{item.phone}</td>
                <td className="border border-gray-300 p-2">
                  <button
                    className="bg-yellow-500 text-white py-1 px-3 rounded mr-2"
                    onClick={() => handleEdit(item.id)}
                  >
                    Modifier
                  </button>
                  <button
                    className="bg-red-500 text-white py-1 px-3 rounded"
                    onClick={() => handleDelete(item.id)}
                  >
                    Supprimer
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
  

export default DiscoverPage;
