import React, { useState } from 'react';
import '../App.css';

function DiscoverPage() {
  const [data, setData] = useState([
    { id: 1, name: 'Jean Dupont', email: 'jean@example.com' },
    { id: 2, name: 'Marie Dubois', email: 'marie@example.com' },
    { id: 3, name: 'Pierre Martin', email: 'pierre@example.com' }
  ]);

  const handleEdit = (id) => {
    alert(`Modifier l'élément avec l'ID : ${id}`);
  };

  const handleDelete = (id) => {
    setData(data.filter(item => item.id !== id));
  };

  return (
    <div className="container">
      <header className="header">
        <h1>Découvrir</h1>
      </header>

      <main className="main-content">
        <h2>Tableau des utilisateurs</h2>
        <table className="table">
          <thead>
            <tr>
              <th>Nom</th>
              <th>Email</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {data.map(item => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>{item.email}</td>
                <td>
                  <button onClick={() => handleEdit(item.id)} className="btn-edit">Modifier</button>
                  <button onClick={() => handleDelete(item.id)} className="btn-delete">Supprimer</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </main>

      <footer className="footer">
        &copy; 2025 Mon Projet. Tous droits réservés.
      </footer>
    </div>
  );
}

export default DiscoverPage;
